package com.zobaer53.diseasesdetection;

import androidx.appcompat.app.AppCompatActivity;
import androidx.appcompat.widget.Toolbar;

import android.graphics.Color;
import android.os.Bundle;
import android.view.View;
import android.widget.TextView;

import com.smarteist.autoimageslider.IndicatorView.animation.type.IndicatorAnimationType;
import com.smarteist.autoimageslider.SliderAnimations;
import com.smarteist.autoimageslider.SliderView;

import java.util.ArrayList;
import java.util.List;

public class ResultActivity extends AppCompatActivity {
    TextView nameTV,genderTV,ageTv,resultTv,accuracyTV;
    SliderAdapter adapter;
    SliderView sliderView;
    List<SliderModel> mSliderList = new ArrayList<>();
    SliderModel sliderItem;
    String[] imageUrl={
            "data:image/jpeg;base64,",
"data:image/jpeg;base64,",
            "data:image/jpeg;base64,"

    };
    String[] description={
            " ",
            " ",
            " "


    };

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_result);

        Toolbar toolbar = findViewById(R.id.toolbar);
        toolbar.setTitle("");
        setSupportActionBar(toolbar);
        toolbar.setNavigationIcon(R.drawable.arrow_back_icon);

        toolbar.setNavigationOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                handleOnBackPress();
            }
        });


        nameTV = findViewById(R.id.nameTV);
        genderTV = findViewById(R.id.genderTV);
        ageTv = findViewById(R.id.ageTV);

        nameTV.setText(getIntent().getStringExtra("name"));
        genderTV.setText(getIntent().getStringExtra("gender"));
        ageTv.setText(getIntent().getStringExtra("age"));


        for (int i=0;i<description.length;i++){
            sliderItem = new SliderModel(description[i],imageUrl[i]);

            mSliderList.add(sliderItem);


        }

        sliderView = findViewById(R.id.imageSlider);
        adapter = new SliderAdapter(mSliderList,this);//pass the title and url list to adapter
        sliderView.setSliderAdapter(adapter);
        sliderView.setIndicatorAnimation(IndicatorAnimationType.WORM); //set indicator animation by using SliderLayout.IndicatorAnimations. :WORM or THIN_WORM or COLOR or DROP or FILL or NONE or SCALE or SCALE_DOWN or SLIDE and SWAP!!
        sliderView.setSliderTransformAnimation(SliderAnimations.SIMPLETRANSFORMATION);
        sliderView.setAutoCycleDirection(SliderView.AUTO_CYCLE_DIRECTION_RIGHT);
        sliderView.setIndicatorSelectedColor(Color.WHITE);
        sliderView.setIndicatorUnselectedColor(Color.GRAY);
        sliderView.setScrollTimeInSec(4); //set scroll delay in seconds :
        sliderView.startAutoCycle();
    }

    private void handleOnBackPress() {
        super.onBackPressed();
    }
}